
def merge_the_tools(string, k):
    list_of_string = list(string)

    list_of_lists = []

    for i in range(0,len(list_of_string),k):
        counter = 0
        list_of_values = []

        while counter < k:
            list_of_values.append(list_of_string[i + counter])
            counter = counter + 1

        list_of_lists.append(list_of_values)

    for k in list_of_lists:
        a = unique(k)
        print(''.join(a))


def unique(sequence):
    seen = set()
    return [x for x in sequence if not (x in seen or seen.add(x))]

merge_the_tools('',3452)
